import React, { useState } from "react";

const CommunityPost = ({ onPost }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [post, setPost] = useState({ nickname: "", rating: 0, content: "" });

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPost({ ...post, [name]: value });
  };

  const handleSubmit = () => {
    // 필요한 경우 입력 필드를 유효성 검사할 수 있습니다

    // 부모 컴포넌트의 onPost 함수를 호출하여 게시글을 전달합니다
    onPost(post);

    // 폼을 초기화하고 모달을 닫습니다
    setPost({ nickname: "", rating: 0, content: "" });
    closeModal();
  };

  return (
    <div>
      <button onClick={openModal}>게시글 작성하기</button>
      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>게시글 작성</h2>
            <label>
              닉네임:
              <input
                type="text"
                name="nickname"
                value={post.nickname}
                onChange={handleInputChange}
              />
            </label>
            <label>
              별점:
              <input
                type="number"
                name="rating"
                value={post.rating}
                onChange={handleInputChange}
              />
            </label>
            <label>
              게시글 내용:
              <textarea
                name="content"
                value={post.content}
                onChange={handleInputChange}
              />
            </label>
            <button onClick={handleSubmit}>등록</button>
            <button onClick={closeModal}>닫기</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CommunityPost;
