// 구현할 것
// 1. 공백 확인 및 양식 확인(한+영, 8글자 이상 확인)
// 2. 백에서 중복여부 넘겨주면 오류 출력

import simple_logo_img from "./assets/simple_logo.PNG";
import "./modal_signup.css";

import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Radio, RadioGroup } from "./RadioGroup";
import { useDispatch } from "react-redux";
import { registerUser } from "./action_user";

function Modalsignup(props) {
  const dispatch = useDispatch();

  const [name, setName] = useState("");
  const [birth, setBirth] = useState("");
  const [gender, setGender] = useState("");
  const [user_id, setUser_id] = useState("");
  const [user_pw, setUser_pw] = useState("");
  const [user_pw2, setUser_pw2] = useState("");
  const [nickname, setNickname] = useState("");

  const onNameHandler = (event) => {
    setName(event.currentTarget.value);
  };
  const onBirthHandler = (event) => {
    setBirth(event.currentTarget.value);
  };
  const onGenderHandler = (event) => {
    setGender(event.currentTarget.value);
  };
  const onUser_idHandler = (event) => {
    setUser_id(event.currentTarget.value);
  };
  const onUser_pwHandler = (event) => {
    setUser_pw(event.currentTarget.value);
  };
  const onUser_pw2Handler = (event) => {
    setUser_pw2(event.currentTarget.value);
  };
  const onNicknameHandler = (event) => {
    setNickname(event.currentTarget.value);
  };
  const onSubmitHandler = (event) => {
    event.preventDefault(); //버튼만 눌렀을 경우 리프레시 되는 것을 막아줌
    console.log("click submit");
    let idCheck = /^[A-Za-z0-9]{8,}$/; //영문+숫자, 8자리 이상 확인
    if (idCheck.test(user_id)) {
    } else {
      user_id.current.focus();
      return;
    }

    let pwCheck = /^[A-Za-z0-9]{8,}$/; //영문+숫자, 8자리 이상 확인
    if (pwCheck.test(user_pw)) {
    } else {
      user_pw.current.focus();
      return;
    }

    if (user_pw !== user_pw2) {
      //비밀번호 확인
      return alert("비밀번호와 비밀번호 확인이 같지 않습니다.");
    }

    let body = {
      name: name,
      birth: birth,
      gender: gender,
      user_id: user_id,
      user_pw: user_pw,
      user_pw2: user_pw2,
      nickname: nickname,
    };

    dispatch(registerUser(body)).then((response) => {
      if (response.payload.success) {
        props.history.push("/");
      } else {
        alert("Error");
      }
    });
  };

  return (
    <Modal
      {...props}
      sige="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <img
          className="simple_logo"
          src={simple_logo_img}
          alt="simple_logo"
        ></img>

        <Modal.Title id="contained-modal-title-vcenter">회원가입</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/*개인정보 입력 칸*/}
        <form onSubmit={onSubmitHandler}>
          <div className="signup_box">
            <div className="signup_input_name">
              <div className="signup_txt input_box">이름</div>
              <input
                id="signup_name"
                className="input_name input"
                type="text"
                value={name}
                onChange={onNameHandler}
                placeholder="홍길동"
                name="signup_name"
              />
            </div>
            <div className="signup_input_birth">
              <div className="signup_txt input_box">생년월일</div>
              <DatePicker
                id="signup_birth"
                className="input_birth input"
                dateFormat={"yyyy.MM.dd"}
                shouldCloseOnSelect //선택하면 자동닫힘
                maxDate={new Date("2009-7-12")} //만 14세 미만 가입 불가
                selected={birth}
                onChange={(date) => setBirth(date)}
                placeholderText={"날짜를 선택하세요"}
              />
            </div>
            <div className="signup_input_gender">
              <div className="signup_txt input_box">성별</div>
              <RadioGroup className="input_radio input">
                <Radio id="radio_btn" name="gender" value="man" defaultChecked>
                  남자
                </Radio>
                <Radio id="radio_btn" name="gender" value="woman">
                  여자
                </Radio>
              </RadioGroup>
            </div>
          </div>
          {/*아이디 및 비밀번호 입력 칸*/}
          <div className="signup_box">
            <div className="signup_input_id">
              <div className="signup_txt input_box">아이디</div>
              <input
                id="signup_id"
                className="input_id input"
                type="text"
                value={user_id}
                onChange={onUser_idHandler}
                placeholder="영문+숫자 8자리 이상"
                name="signup_id"
              />
            </div>
            <div className="signup_input_pw">
              <div className="signup_txt input_box">비밀번호</div>
              <input
                id="signup_pw"
                className="input_pw input"
                type="password"
                value={user_pw}
                onChange={onUser_pwHandler}
                placeholder="영문+숫자 8자리 이상"
                name="signup_pw"
              />
            </div>
            <div className="signup_input_pw2">
              <div className="signup_txt input_box">비밀번호 확인</div>
              <input
                id="signup_pw2"
                className="input_pw input"
                type="password"
                value={user_pw2}
                onChange={onUser_pw2Handler}
                placeholder="비밀번호 확인"
                name="signup_pw2"
              />
            </div>
            <div className="signup_input_id">
              <div className="signup_txt input_box">닉네임</div>
              <input
                id="signup_nickname"
                className="input_nickname input"
                type="text"
                value={nickname}
                onChange={onNicknameHandler}
                placeholder="닉네임"
                name="signup_nickname"
              />
            </div>
          </div>
          <div className="btn_signup">
            <button id="btn_signup_ok" formAction="">
              가입하기
            </button>
          </div>
        </form>
      </Modal.Body>
      <Modal.Footer></Modal.Footer>
    </Modal>
  );
}
export default Modalsignup;
