import React from "react";
import GridLayout from "react-grid-layout";
import styled from "styled-components";

const layout = [
  { i: "Japan", x: 0, y: 0, w: 1, h: 1 },
  { i: "Vietnam", x: 1, y: 0, w: 1, h: 1 },
  { i: "Jeju", x: 2, y: 0, w: 1, h: 1 },
  { i: "LA", x: 3, y: 0, w: 1, h: 1 },
  { i: "+", x: 4, y: 0, w: 1, h: 1 },
];

const GridItemWrapper = styled.div`
  background: #f5f5f5;
  cursor: pointer; /* 커서 모양을 포인터로 변경하여 클릭 가능한 요소임을 표시 */
`;

const GridItemContent = styled.div`
  padding: 8px;
`;

const Root = styled.div`
  padding: 16px;
`;

const GridComponent = () => {
  const handleGridItemClick = (i) => {
    // 각 그리드 박스 요소 클릭 시 처리할 로직을 작성
    // 예: 특정 URL로 이동하거나 다른 동작을 수행
    console.log("Clicked:", i);
    const url = "https://tripplanner.com/${i}";
    window.location.href = url;
  };

  return (
    <Root>
      <GridLayout layout={layout} cols={5} rowHeight={300} width={1000}>
        {layout.map((item) => (
          <GridItemWrapper
            key={item.i}
            onClick={() => handleGridItemClick(item.i)} // 클릭 이벤트 처리
          >
            <GridItemContent>{item.i}</GridItemContent>
          </GridItemWrapper>
        ))}
      </GridLayout>
    </Root>
  );
};

export default GridComponent;
