import "./login.css";
import { useState } from "react";
import logo_img from "./assets/logo.PNG";

function LoginPage() {
  //모달창 state
  const [modalOpen, setModalOpen] = useState(false);

  //모달창 show
  const showModal = () => {
    setModalOpen(true);
  };
  const closeModal = () => {
    setModalOpen(false);
  };
  return (
    <div class="mainbox">
      <div className="home-container">
        <div className="img_box">
          <div class="img_airplane"></div>
        </div>
        <div className="login_box">
          <div class="home_box1 ">
            <img class="img_logo" src={logo_img} alt="logo"></img>
            <form class="login_form" method="get" action="main">
              <div class="home_box2 box">
                <input
                  id="login_id"
                  class="input_login"
                  type="text"
                  placeholder="user ID"
                  name="button_id"
                />
              </div>
              <div class="home_box2 box">
                <input
                  id="login_pw"
                  class="input_login"
                  type="password"
                  placeholder="user PW"
                  name="button_pw"
                />
              </div>
              <input
                id="login_btn"
                class="btn_login box"
                type="submit"
                disabled
                value="sign in"
              />
            </form>
          </div>
          <div class="home_box3">
            <div class="sign-up">
              <button onClick={showModal}>sign up</button> {/*회원가입 버튼*/}
              <Modal open={modalOpen} close={closeModal} header="Modal header">
                모달창 내용
              </Modal>
            </div>
          </div>
        </div>
      </div>
      <footer class="footer">
        <div class="footer_txt">ⓒ 2023. OYR_PROJECT All Rights Reserved.</div>
        <div class="footer_txt">OpenYearRound@naver.com</div>
      </footer>
    </div>
  );
}

export default LoginPage;
