import React from "react";
import GridLayout from "react-grid-layout";

import styled from "styled-components";

const layout = [
  { i: "blue-eyes-dragon", x: 0, y: 0, w: 1, h: 1 },
  { i: "dark-magician", x: 1, y: 0, w: 1, h: 1 },
  { i: "kuriboh", x: 2, y: 0, w: 1, h: 1 },
  { i: "spell-caster", x: 3, y: 0, w: 1, h: 1 },
  { i: "summoned-skull", x: 4, y: 0, w: 1, h: 1 },
];

const GridItemWrapper = styled.div`
  background: #f5f5f5;
`;

const GridItemContent = styled.div`
  padding: 8px;
`;

const Root = styled.div`
  padding: 16px;
`;

const GridComponent = () => {
  return (
    <Root>
      <GridLayout layout={layout} cols={5} rowHeight={300} width={1000}>
        {layout.map((item) => (
          <GridItemWrapper key={item.i}>
            <GridItemContent>{item.i}</GridItemContent>
          </GridItemWrapper>
        ))}
      </GridLayout>
    </Root>
  );
};

export default GridComponent; // 수정: GridComponent로 내보내기
