// eslint-disable-next-line
import simple_logo_img from "./assets/simple_logo.PNG";
import "./modal_signup.css";

import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Radio, RadioGroup } from "@sajari/react-components";

function Modal_signup(props) {
  const [selectedDate, setSelectedDate] = useState("");
  const [gender_value, gender_setValue] = useState("");

  return (
    <Modal
      {...props}
      sige="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <img
          className="simple_logo"
          src={simple_logo_img}
          alt="simple_logo"
        ></img>

        <Modal.Title id="contained-modal-title-vcenter">회원가입</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="signup_box">
          <div className="signup_input_name">
            <div className="signup_txt input_box">이름</div>
            <input
              id="signup_name"
              className="input_name input"
              type="text"
              placeholder="홍길동"
              name="signup_name"
            />
          </div>
          <div className="signup_input_birth">
            <div className="signup_txt input_box">생년월일</div>
            <DatePicker
              id="signup_birth"
              className="input_birth input"
              dateFormat={"yyyy.MM.dd"}
              shouldCloseOnSelect //선택하면 자동닫힘
              maxDate={new Date("2009-7-12")} //만 14세 미만 가입 불가
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              placeholderText={"날짜를 선택하세요"}
            />
          </div>
          <div className="signup_input_gender">
            <div className="signup_txt input_box">성별</div>
            <RadioGroup
              onChange={(e) => gender_setValue(e.target.value)}
              value={gender_value}
              name="gender_Radio"
            >
              <Radio value="남">남자</Radio>
              <Radio value="여">여자</Radio>
            </RadioGroup>
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button id="btn_signup_ok" onClick={props.onHide}>
          가입하기
        </Button>
      </Modal.Footer>
    </Modal>
  );
}
export default Modal_signup;
