import React, { useState, useEffect } from 'react';


function Weatherapi(){
    const [coords, saveCoords] = useState();
    const [temp, setTemp] = useState();
    const [weather, setWeather] = useState();

    function handleGeoSucc(position) {
        console.log(position);
        const latitude = position.coords.latitude;  // 경도  
        const longitude = position.coords.longitude;  // 위도
        const coordsObj = {
        latitude,
        longitude
        }
        saveCoords(coordsObj);
        getWeather(latitude, longitude);
    }

    function handleGeoErr(err) {
        console.log("geo err! " + err);
    }

    function requestCoords() {
        navigator.geolocation.getCurrentPosition(handleGeoSucc, handleGeoErr);
    }

    function getWeather(lat, lon) {
        fetch(`https://api.openweathermap.org/data/3.0/onecall?lat=33.44&lon=-94.04&exclude=hourly,daily&appid={136b4fe7c4391681560aa24f534b2b08}&units=metric`)
        .then(res => res.json())
        .then(data => {
            console.log(data);
            if (data && data.main && data.main.temp && data.weather && data.weather.length > 0) {
                const temp = data.main.temp;
                const weathers = data.weather[data.weather.length - 1];
                setTemp(temp);
                setWeather(weathers.main);
            } else {
                console.log('Weather data is incomplete.');
            }
        })
        .catch(error => {
            console.log('Error fetching weather data:', error);
        });
    }

    useEffect(() => {
        requestCoords();
    }, []);
}

export default Weatherapi;
