                                        style={{
                                            top: isDataEmpty ? '0%' : `${44 + item.topmargin * 0.8}px`, // 여기서 item.topmargin을 0.6을 곱한 후에 44를 더해줍니다.
                                            display: isDataEmpty ? 'none' : 'block',
                                            height: isDataEmpty ? '0px' : `${item.subtime * 0.8}px`,
                                        }}
                                    >
                                        <p>위치: {item.start}</p>
                                    </div>
                                );
                            }
                            return null;
                        })}
                    </div>
                    <div className='schdule-item align-center border-right-gray'>
                        <div className='week-top align-center'>토</div>

                        {data.map((item) => {
                            if (item.day === 7) {
                                return (
                                <div
                                    key={item.index}
                                    className={`schdule-content-add ${isDataEmpty ? 'hidden' : ''}`}
                                    style={{
                                    top: isDataEmpty ? '10%' : `${item.starttime}%`,
                                    display: isDataEmpty ? 'none' : 'block',
                                    height: isDataEmpty ? '0px' : `${item.endtime - item.starttime}%`,
                                    }}
                                >
                                    <p>위치: {item.start}</p>
                                </div>
