import React, { useState, useEffect } from 'react';
import './ScheduleItem.css';

function ScheduleEditModal({ isOpen, closeModal, onSave, editedSchedule }) {
  const [selectedPlace, setSelectedPlace] = useState('');
  const [startday, setStartday] = useState('');
  const [starttime, setStarttime] = useState('');
  const [endtime, setEndtime] = useState('');

  useEffect(() => {
    if (editedSchedule) {
      setSelectedPlace(editedSchedule.start);
      setStartday(editedSchedule.startday);
      setStarttime(editedSchedule.starttime);
      setEndtime(editedSchedule.endtime);
    } else {
      setSelectedPlace('');
      setStartday('');
      setStarttime('');
      setEndtime('');
    }
  }, [editedSchedule]);

  const handleChangePlace = (event) => setSelectedPlace(event.target.value);
  const handleChangeStartday = (event) => setStartday(event.target.value);
  const handleChangeStarttime = (event) => setStarttime(event.target.value);
  const handleChangeEndtime = (event) => setEndtime(event.target.value);

  const handleSave = () => {
    // Validation and saving logic here
    // You can send the updated schedule data to the server or update it locally
    const updatedSchedule = {
      start: selectedPlace,
      startday,
      starttime,
      endtime,
      // Add other properties as needed
    };

    onSave(updatedSchedule); // Pass the updated schedule data to the parent component
    closeModal();
  };

  return (
    <div className={`modal ${isOpen ? 'open' : ''}`}>
      <div className="modal-content">
        <h2>Edit Schedule</h2>
        <form>
          <div className="form-group">
            <label>Place</label>
            <input
              type="text"
              value={selectedPlace}
              onChange={handleChangePlace}
            />
          </div>
          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              value={startday}
              onChange={handleChangeStartday}
            />
          </div>
          <div className="form-group">
            <label>Start Time</label>
            <input
              type="time"
              value={starttime}
              onChange={handleChangeStarttime}
            />
          </div>
          <div className="form-group">
            <label>End Time</label>
            <input
              type="time"
              value={endtime}
              onChange={handleChangeEndtime}
            />
          </div>
        </form>
        <div className="modal-actions">
          <button onClick={handleSave}>Save</button>
          <button onClick={closeModal}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default ScheduleEditModal;
