import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';

const Googlemap = () => {
  const [directions, setDirections] = useState([]);

  useEffect(() => {
    const url = `/maps/api/directions/json?origin=Boston%2C%20MA&destination=Concord%2C%20MA&waypoints=via%3ACharlestown%2CMA%7Cvia%3ALexington%2CMA&departure_time=now&key=AIzaSyAyZffxPv5S2NP2NEp_31-HH9XmDwSEWbM`;

    axios
      .get(url)
      .then((response) => {
        console.log(response);
        const result = response.data;
        if (result.routes.length > 0) {
          setDirections([result.routes[0]]); // 지도에 표시할 방향 정보를 배열로 감싸서 설정
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const apiKey = 'AIzaSyAyZffxPv5S2NP2NEp_31-HH9XmDwSEWbM';

  const mapStyles = {
    height: '400px',
    width: '100%',
  };

  const defaultCenter = {
    lat: 42.3601, // 보스턴의 위도
    lng: -71.0589, // 보스턴의 경도
  };

  return (
    <div>
      <LoadScript googleMapsApiKey={apiKey}>
        <GoogleMap mapContainerStyle={mapStyles} zoom={13} center={defaultCenter}>
          {directions.map((direction, index) => (
            <DirectionsRenderer key={index} directions={direction} />
          ))}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Googlemap;
