import React, { useState } from 'react';
import './PlannerWeather.css';

function PlannerWeather() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="planner-weather">
      <h2>날씨별 옷차림 추천</h2>
      <div className="weather-categories">
        <div className="weather-category">
          <h3>-10°C ~ 0°C</h3>
          <p>겨울 외투: 패딩, 다운 재킷, 울 코트 등</p>
          <p>두꺼운 니트웨어 또는 기모 내의</p>
          <p>목도리, 장갑, 비니 등의 액세서리</p>
          <p>두꺼운 부츠 또는 방수 부츠</p>
        </div>
        {/* 나머지 날씨별 옷차림 정보도 동일한 형식으로 추가하세요 */}
      </div>
      <button className="btn-open-modal" onClick={toggleModal}>
        기상청에서 날씨정보 확인하기
      </button>
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="btn-close-modal" onClick={toggleModal}>
              &times;
            </span>
            <p>
              날씨 정보를 확인하려면{' '}
              <a
                href="https://www.weather.go.kr/w/theme/world-weather.do"
                target="_blank"
                rel="noopener noreferrer"
              >
                여기
              </a>
              를 클릭하세요.
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default PlannerWeather;
