// PlannerWeather.js
import React, { useState } from 'react';
import './PlannerWeather.css';

const PlannerWeather = () => {
  const [temperature, setTemperature] = useState(20); // 예시로 초기 온도 설정

  const handleTemperatureChange = (event) => {
    const newTemperature = parseInt(event.target.value);
    setTemperature(newTemperature);
  };

  const getRecommendedClothes = () => {
    if (temperature <= 0) {
      return (
        <div>
        겨울 외투: 패딩, 다운 재킷, 울 코트 등
        두꺼운 니트웨어 또는 기모 내의
        목도리, 장갑, 비니 등의 액세서리
        두꺼운 부츠 또는 방수 부츠
        </div>
      );
    } else if (temperature <= 10) {
      return (
        <div>
          겨울 코트 또는 두꺼운 점퍼
니트 풀오버 또는 스웨터
청바지, 슬랙스 등 따뜻한 팬츠
목도리나 비니와 같은 액세서리
        </div>
      );
    } else if (temperature <= 20) {
      return (
        <div>
          가벼운 자켓, 블레이저 또는 카디건
긴팔 티셔츠, 블라우스, 얇은 니트 등
슬랙스, 치노 팬츠, 스커트 등
가벼운 스카프 또는 액세서리
        </div>
      );
    } else if (temperature <= 30) {
      return (
        <div>
          반팔 티셔츠, 블라우스, 탱크탑 등
반바지, 스커트, 가벼운 팬츠 등
모자, 선글라스, 선크림 등의 햇빛 차단용품
        </div>
      );
    } else {
      return (
        <div>
         가볍고 통기성 있는 반팔 티셔츠, 원피스 등
반바지, 린넨 바지 등
모자와 선글라스, 선크림 등의 햇빛 차단용품
        </div>
      );
    }
  };

  return (
    <div className="planner-weather">
      <h2>오늘의 옷차림 추천</h2>
      <input
        type="number"
        value={temperature}
        onChange={handleTemperatureChange}
        placeholder="현재 온도 (°C)"
      />
      {getRecommendedClothes()}
      <a
        href="https://www.weather.go.kr/w/theme/world-weather.do"
        target="_blank"
        rel="noopener noreferrer"
        className="weather-link"
      >
        기상청에서 날씨정보 확인하기
      </a>
    </div>
  );
};

export default PlannerWeather;
