import React, { useEffect, useState } from 'react';
import './ScheduleItem.css';

function ScheduleItem({ isOpen, closeModal, onSave }) {
  const [selectedPlace, setSelectedPlace] = useState('');
  const [startday, setStartday] = useState('');
  const [starttime, setStarttime] = useState('');
  const [endday, setEndday] = useState('');
  const [endtime, setEndtime] = useState('');

  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
  }, [isOpen]);

  const handleSave = (event) => {
    event.preventDefault();

    onSave({
      start: selectedPlace,
      startday,
      starttime,
      endday,
      endtime,
    });
  };

  return (
    <div className="modal-container" style={{ display: isOpen ? 'block' : 'none' }}>
      <div className="modal-background" onClick={closeModal}></div>
      <div className="modal-content">
        <h2 className="modal-title">일정 추가</h2>
        <form onSubmit={handleSave}>
          <div className="form-group">
            <label htmlFor="selectedPlace">장소</label>
            <input
              type="text"
              id="selectedPlace"
              className="form-control"
              value={selectedPlace}
              onChange={(e) => setSelectedPlace(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="startday">시작 날짜</label>
            <input
              type="date"
              id="startday"
              className="form-control"
              value={startday}
              onChange={(e) => setStartday(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="starttime">시작 시간</label>
            <input
              type="time"
              id="starttime"
              className="form-control"
              value={starttime}
              onChange={(e) => setStarttime(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="endday">종료 날짜</label>
            <input
              type="date"
              id="endday"
              className="form-control"
              value={endday}
              onChange={(e) => setEndday(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="endtime">종료 시간</label>
            <input
              type="time"
              id="endtime"
              className="form-control"
              value={endtime}
              onChange={(e) => setEndtime(e.target.value)}
            />
          </div>
          <div className="map-container">
            <div id="map"></div>
          </div>
          <button type="submit" className="btn btn-primary submit-btn">제출</button>
        </form>
      </div>
    </div>
  );
}

export default ScheduleItem;
