import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';

const Googlemap = () => {
  const [directions, setDirections] = useState(null); // null로 초기화

  useEffect(() => {
    const url = `/maps/api/directions/json?origin=Boston%2C%20MA&destination=Concord%2C%20MA&waypoints=via%3ACharlestown%2CMA%7Cvia%3ALexington%2CMA&departure_time=now&key=AIzaSyAyZffxPv5S2NP2NEp_31`;

    axios
      .get(url)
      .then((response) => {
        const result = response.data;
        if (result.routes.length > 0) {
          setDirections(result.routes[0]); // DirectionsRenderer에 바로 DirectionsResult 객체를 설정
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const apiKey = 'AIzaSyAyZffxPv5S2NP2NEp_31-HH9XmDwSEWbM';

  const mapStyles = {
    height: '400px',
    width: '100%',
  };

  const defaultCenter = {
    lat: 42.3601, // 보스턴의 위도
    lng: -71.0589, // 보스턴의 경도
  };

  const redPolylineOptions = {
    strokeColor: '#FF0000',
    strokeOpacity: 1,
    strokeWeight: 4,
  };

  return (
    <div>
      <LoadScript googleMapsApiKey={apiKey}>
        <GoogleMap mapContainerStyle={mapStyles} zoom={13} center={defaultCenter}>
          {directions && (
            <DirectionsRenderer
              directions={directions} // 수정된 부분: directions prop에 직접 객체 전달
              options={{ polylineOptions: redPolylineOptions }}
            />
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Googlemap;
