import React, { useEffect } from 'react';
import './ScheduleModal.css';

function ScheduleModal({ isOpen, closeModal, scheduleData }) {
  useEffect(() => {
    if (isOpen) {
      // 모달이 열릴 때 body 스크롤을 막음
      document.body.style.overflow = 'hidden';
    } else {
      // 모달이 닫힐 때 스크롤을 다시 활성화
      document.body.style.overflow = 'auto';
    }

    // 컴포넌트 언마운트 시에 스크롤을 다시 활성화
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isOpen]);

  if (!isOpen) {
    return null;
  }

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{scheduleData.start}</h2>
        <p>일정 시작시간: {scheduleData.starttime}</p>
        <p>일정 종료시간: {scheduleData.endtime}</p>
        <button onClick={closeModal}>닫기</button>
      </div>
    </div>
  );
}

export default ScheduleModal;
