import './ExpenseCalculationAndSupplies.css'
import { useNavigate } from "react-router-dom";
import React, { useState, useEffect } from 'react';

function ExpenseCalculationAndSupplies() {
    const navigate = useNavigate();

    const goTomain = () => {
        navigate("/main");
    }

    const [data, setData] = useState([]);

    const addData = (newData) => {
        newData.index = data.length + 1;

        setData((prevData) => [...prevData, newData]);
      };

      useEffect(() => {
        console.log(data);
      }, [data]); 


    const [showModal, setShowModal] = useState(false);
    const [expenseTitle, setExpenseTitle] = useState('');
    const [expenseAmount, setExpenseAmount] = useState('');

    const openModal = () => {
        setShowModal(true);
    };

    const closeModal = () => {
        setShowModal(false);
        setExpenseTitle('');
        setExpenseAmount('');
    };

    const handleExpenseTitleChange = (event) => {
        setExpenseTitle(event.target.value);
    };

    const handleExpenseAmountChange = (event) => {
        setExpenseAmount(event.target.value);
    };

    const handleAddExpense = () => {
        if (expenseTitle && expenseAmount) {
        const newData = {
            title: expenseTitle,
            amount: expenseAmount
        };
        addData(newData);
        closeModal();
        }
    };

    const [showModal2, setShowModal2] = useState(false);
    const [expenseTitle2, setExpenseTitle2] = useState('');

    const openModal2 = () => {
        setShowModal2(true);
    };

    const closeModal2 = () => {
        setShowModal2(false);
        setExpenseTitle2('');
    };

    const handleExpenseTitleChange2 = (event) => {
        setExpenseTitle2(event.target.value);
    };


    const handleAddExpense2 = () => {
        if (expenseTitle2) {
        const newData = {
            title: expenseTitle2
        };
        addData(newData);
        closeModal();
        }
    };

    return (<div className='All align-center'>
        <header className='header-set align-center'>
            <div className='Title'> </div>
            <div className='Tag-list'>
                <div className='Tag'>홈</div>
                <div className='Tag' onClick={goTomain}>플래너</div>
                <div className='Tag'>경비계산 / 준비물</div>
                <div className='Tag'>관광지</div>
                <div className='Tag'>커뮤니티</div>
                <div className='Tag'>오류문의</div>
            </div>
        </header>

        <div className='main-set'>
            <div className='calculator-set border-right'>
                <div className='expenseCalculatorTitle'>플래너1 경비 계산</div>
                <div className='expenseCalculatoradddiv'>
                    <button className='expenseCalculatoraddbtn' onClick={openModal}>항목 추가</button>
                </div>
                <div className='expenseCalculator'> 
                    {data.map((item) => (
                    <div className='expense-item' key={item.index}>
                        <div className='expense-item-title'>{item.title}</div>
                        <div className='expense-item-amount'>{item.amount}원</div>
                    </div>
                    ))}
                    <div className='expense-item'>
                        <div className='expense-item-title'>합계</div>
                        <div className='expense-item-amount'>
                        {data.reduce((total, item) => total + parseInt(item.amount), 0)} 원
                        </div>
                    </div>
                </div>
            </div>

            <div className='calculator-set'>
                <div className='align-center expenseCalculatorTitle'>플래너1 준비물</div>
                <div className='align-center expenseCalculatoradddiv'>
                    <button className='noteaddbtn' onClick={openModal2}>항목 추가</button>
                </div>
                <div className='expenseCalculator'> 
                {data.map((item) => (
                    <div className='expense-item' key={item.index}>
                        <div className='note-item-check'>V</div>
                        <div className='note-item-title'>{item.title}</div>
                    </div>
                    ))}
                </div>
            </div>

        {showModal && (
            <div className='modal'>
            <div className='modal-content'>
                <span className='close' onClick={closeModal}>
                &times;
                </span>
                <div>
                <h2>경비 항목 추가</h2>
                <input
                    type='text'
                    placeholder='항목명'
                    value={expenseTitle}
                    onChange={handleExpenseTitleChange}
                />
                <input
                    type='number'
                    placeholder='비용'
                    value={expenseAmount}
                    onChange={handleExpenseAmountChange}
                />
                <button onClick={handleAddExpense}>추가</button>
                </div>
            </div>
            </div>
        )}

        {showModal2 && (
            <div className='modal'>
            <div className='modal-content'>
                <span className='close' onClick={closeModal2}>
                &times;
                </span>
                <div>
                <h2>경비 항목 추가</h2>
                <input
                    type='text'
                    placeholder='항목명'
                    value={expenseTitle2}
                    onChange={handleExpenseTitleChange2}
                />
                <button onClick={handleAddExpense2}>추가</button>
                </div>
            </div>
            </div>
        )}

        </div>

        <footer className='footer-set'>
            <div>create by OYR 1조</div>
            <div>email: OpenYearRound@naver.com</div> 
        </footer>
    </div>)
}

export default ExpenseCalculationAndSupplies;
