import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, DirectionsService, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';

const Googlemap = () => {
  const [directions, setDirections] = useState(null);
  const [origin, setOrigin] = useState(null);
  const [destination, setDestination] = useState(null);
  const [waypoints, setWaypoints] = useState(null);

  useEffect(() => {
    const url = `/maps/api/directions/json?origin=Boston%2C%20MA&destination=Concord%2C%20MA&waypoints=via%3ACharlestown%2CMA%7Cvia%3ALexington%2CMA&departure_time=now&key=AIzaSyAyZffxPv5S2NP2NEp_31-HH9XmDwSEWbM`;

    axios
      .get(url)
      .then((response) => {
        const result = response.data;
        if (result.routes.length > 0) {
          const route = result.routes[0];
          setDirections(route);
          setOrigin(route.legs[0].start_location);
          setDestination(route.legs[0].end_location);
          setWaypoints(route.waypoint_order.map(index => route.legs[index].end_location));
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const apiKey = 'AIzaSyAyZffxPv5S2NP2NEp_31-HH9XmDwSEWbM'; // 여기에 자신의 Google Maps API 키를 입력하세요

  const mapStyles = {
    height: '400px',
    width: '100%',
  };

  const defaultCenter = {
    lat: 42.3601, // 보스턴의 위도
    lng: -71.0589, // 보스턴의 경도
  };

  return (
    <div>
      <LoadScript googleMapsApiKey={apiKey}>
        <GoogleMap mapContainerStyle={mapStyles} zoom={13} center={defaultCenter}>
          {directions && (
            <>
              <DirectionsRenderer
                directions={directions}
                options={{ polylineOptions: { strokeColor: '#FF0000', strokeWeight: 4 } }}
              />
              <DirectionsService
                options={{
                  origin,
                  destination,
                  waypoints,
                  travelMode: 'DRIVING',
                }}
                callback={(result) => {
                  if (result !== null) {
                    setDirections(result);
                  }
                }}
              />
            </>
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Googlemap;
