import React, { useEffect, useState } from 'react';

const Googlemap = () => {
  const [originPlaceId, setOriginPlaceId] = useState("");
  const [destinationPlaceId, setDestinationPlaceId] = useState("");
  const [travelMode, setTravelMode] = useState(google.maps.TravelMode.WALKING);
  const [directionsRenderer, setDirectionsRenderer] = useState(null);

  useEffect(() => {
    window.onload = initMap;

    const map = new window.google.maps.Map(document.getElementById("map"),{
      mapTypeControl: false,
      center: { lat: -33.8688, lng: 151.2195 },
      zoom: 13,
    });

    const directionsRenderer = new google.maps.DirectionsRenderer();
    directionsRenderer.setMap(map);
    setDirectionsRenderer(directionsRenderer);

    const originInput = document.getElementById("origin-input");
    const destinationInput = document.getElementById("destination-input");
    const modeSelector = document.getElementById("mode-selector");

    const originAutocomplete = new google.maps.places.Autocomplete(
      originInput,
      { fields: ["place_id"] },
    );

    const destinationAutocomplete = new google.maps.places.Autocomplete(
      destinationInput,
      { fields: ["place_id"] },
    );

    setupClickListener("changemode-walking", google.maps.TravelMode.WALKING);
    setupClickListener("changemode-transit", google.maps.TravelMode.TRANSIT);
    setupClickListener("changemode-driving", google.maps.TravelMode.DRIVING);
    setupPlaceChangedListener(originAutocomplete, "ORIG");
    setupPlaceChangedListener(destinationAutocomplete, "DEST");

    map.controls[google.maps.ControlPosition.TOP_LEFT].push(originInput);
    map.controls[google.maps.ControlPosition.TOP_LEFT].push(destinationInput);
    map.controls[google.maps.ControlPosition.TOP_LEFT].push(modeSelector);
  }, []);

  const setupClickListener = (id, mode) => {
    const radioButton = document.getElementById(id);

    radioButton.addEventListener("click", () => {
      setTravelMode(mode);
      route();
    });
  };

  const setupPlaceChangedListener = (autocomplete, mode) => {
    autocomplete.bindTo("bounds", google.map);
    autocomplete.addListener("place_changed", () => {
      const place = autocomplete.getPlace();

      if (!place.place_id) {
        window.alert("Please select an option from the dropdown list.");
        return;
      }

      if (mode === "ORIG") {
        setOriginPlaceId(place.place_id);
      } else {
        setDestinationPlaceId(place.place_id);
      }

      route();
    });
  };

  const route = () => {
    if (!originPlaceId || !destinationPlaceId) {
      return;
    }

    const directionsService = new google.maps.DirectionsService();
    directionsService.route(
      {
        origin: { placeId: originPlaceId },
        destination: { placeId: destinationPlaceId },
        travelMode: travelMode,
      },
      (response, status) => {
        if (status === "OK") {
          directionsRenderer.setDirections(response);
        } else {
          window.alert("Directions request failed due to " + status);
        }
      },
    );
  };

  return (
    <div>
      <input id="origin-input" placeholder="Origin" />
      <input id="destination-input" placeholder="Destination" />
      <div id="mode-selector">
        <input
          type="radio"
          name="type"
          id="changemode-walking"
          defaultChecked
        />
        <label htmlFor="changemode-walking">Walking</label>
        <input type="radio" name="type" id="changemode-transit" />
        <label htmlFor="changemode-transit">Transit</label>
        <input type="radio" name="type" id="changemode-driving" />
        <label htmlFor="changemode-driving">Driving</label>
      </div>
      <div id="map" style={{ height: "400px", width: "100%" }}></div>
    </div>
  );
};

export default Googlemap;
