import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './ExpenseCalculationAndSupplies.module.css';

function ExpenseCalculationAndSupplies() {
    const navigate = useNavigate();

    const goTomain = () => {
        navigate('/main');
    }

    const [expenseData, setExpenseData] = useState([]);
    const [suppliesData, setSuppliesData] = useState([]);

    // 경비 계산 모달창
    const [showExpenseModal, setShowExpenseModal] = useState(false);
    const [expenseTitle, setExpenseTitle] = useState('');
    const [expenseAmount, setExpenseAmount] = useState('');

    const openExpenseModal = () => {
        setShowExpenseModal(true);
    };

    const closeExpenseModal = () => {
        setShowExpenseModal(false);
        setExpenseTitle('');
        setExpenseAmount('');
    };

    const handleExpenseTitleChange = (event) => {
        setExpenseTitle(event.target.value);
    };

    const handleExpenseAmountChange = (event) => {
        setExpenseAmount(event.target.value);
    };

    const handleAddExpense = async () => {
        if (expenseTitle && expenseAmount) {
            const newExpense = {
                title: expenseTitle,
                amount: expenseAmount
            };
            setExpenseData([...expenseData, newExpense]);
            closeExpenseModal();

            try {
                await axios.post('/api/expense', newExpense);
            } catch (error) {
                console.error('Error adding expense:', error);
            }
        }
    };

    // 준비물 입력 모달창
    const [showSuppliesModal, setShowSuppliesModal] = useState(false);
    const [suppliesTitle, setSuppliesTitle] = useState('');

    const openSuppliesModal = () => {
        setShowSuppliesModal(true);
    };

    const closeSuppliesModal = () => {
        setShowSuppliesModal(false);
        setSuppliesTitle('');
    };

    const handleSuppliesTitleChange = (event) => {
        setSuppliesTitle(event.target.value);
    };

    const handleAddSupplies = async () => {
        if (suppliesTitle) {
            const newSupplies = {
                title: suppliesTitle,
                checked: false
            };
            setSuppliesData([...suppliesData, newSupplies]);
            closeSuppliesModal();

            try {
                await axios.post('/api/essentials', newSupplies);
            } catch (error) {
                console.error('Error adding supplies:', error);
            }
        }
    };

    const handleCheckChange = (index) => {
        const updatedSupplies = suppliesData.map((item, i) => {
            if (i === index) {
                return {
                    ...item,
                    checked: !item.checked
                };
            }
            return item;
        });

        setSuppliesData(updatedSupplies);
    };

    const [isOverflow, setIsOverflow] = useState(false);

    useEffect(() => {
        const expenseCalculator = document.querySelector(`.${styles.expenseCalculator}`);
        if (expenseCalculator.scrollHeight > expenseCalculator.clientHeight) {
            setIsOverflow(true);
        } else {
            setIsOverflow(false);
        }
    }, [expenseData]);

    return (
        <div className={`${styles.All} ${styles['align-center']}`}>
            <header className={`${styles['header-set']} ${styles['align-center']}`}>
                <div className={styles.Title}></div>
                <div className={styles['Tag-list']}>
                    <div className={styles.Tag}>홈</div>
                    <div className={styles.Tag} onClick={goTomain}>플래너</div>
                    <div className={styles.Tag}>경비계산 / 준비물</div>
                    <div className={styles.Tag}>관광지</div>
                    <div className={styles.Tag}>커뮤니티</div>
                    <div className={styles.Tag}>오류문의</div>
                </div>
            </header>

            <div className={`${styles['main-set']}`}>
                <div className={`${styles['calculator-set']} ${styles['border-right']}`}>
                    <div className={styles['expenseCalculatorTitle']}>플래너1 경비 계산</div>
                    <div className={styles['expenseCalculatoradddiv']}>
                        <button className={styles['expenseCalculatoraddbtn']} onClick={openExpenseModal}>항목 추가</button>
                    </div>
                    <div className={styles['expenseCalculator']}>
                        {expenseData.map((item, index) => (
                            <div className={styles['expense-item']} key={index}>
                                <div className={styles['expense-item-title']}>{item.title}</div>
                                <div className={styles['expense-item-amount']}>{item.amount}원</div>
                            </div>
                        ))}
                        <div className={styles['expense-item']}>
                            <div className={styles['expense-item-title']}>합계</div>
                            <div className={styles['expense-item-amount']}>
                                {expenseData.reduce((total, item) => total + parseInt(item.amount), 0)} 원
                            </div>
                        </div>
                    </div>
                </div>
                <div className={`${styles['calculator-set']}`}>
                    <div className={`${styles['align-center']} ${styles['expenseCalculatorTitle']}`}>플래너1 준비물</div>
                    <div className={`${styles['align-center']} ${styles['expenseCalculatoradddiv']}`}>
                        <button className={styles['noteaddbtn']} onClick={openSuppliesModal}>항목 추가</button>
                    </div>
                    <div className={styles['expenseCalculator']}>
                        {suppliesData.map((item, index) => (
                            <div className={styles['expense-item']} key={index}>
                                <input
                                    type='checkbox'
                                    className={styles['note-item-check']}
                                    checked={item.checked}
                                    onChange={() => handleCheckChange(index)}
                                />
                                <div className={styles['note-item-title']}>{item.title}</div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {showExpenseModal && (
                <div className={styles['modal']}>
                    <div className={styles['modal-content']}>
                        <span className={styles['close']} onClick={closeExpenseModal}>
                            &times;
                        </span>
                        <div>
                            <h2>경비 항목 추가</h2>
                            <input
                                type='text'
                                placeholder='항목명'
                                value={expenseTitle}
                                onChange={handleExpenseTitleChange}
                            />
                            <input
                                type='number'
                                placeholder='비용'
                                value={expenseAmount}
                                onChange={handleExpenseAmountChange}
                            />
                            <button onClick={handleAddExpense}>추가</button>
                        </div>
                    </div>
                </div>
            )}

            {showSuppliesModal && (
                <div className={styles['modal']}>
                    <div className={styles['modal-content']}>
                        <span className={styles['close']} onClick={closeSuppliesModal}>
                            &times;
                        </span>
                        <div>
                            <h2>준비물 항목 추가</h2>
                            <input
                                type='text'
                                placeholder='항목명'
                                defaultValue={suppliesTitle} 
                                onChange={handleSuppliesTitleChange}
                            />
                            <button onClick={handleAddSupplies}>추가</button>
                        </div>
                    </div>
                </div>
            )}
            <footer className={styles['footer-set']}>
                <div>create by OYR 1조</div>
                <div>email: OpenYearRound@naver.com</div> 
            </footer>
        </div>
    );
}

export default ExpenseCalculationAndSupplies;
